select * from customer limit 20;

#what is the total revenue generated by male vs. female customers

select gender, sum(purchase_amount) as revenue
from customer
group by gender

#which customers used a discount but still spent more than the average purchase amount?

select customer_id, purchase_amount
from customer
where discount_applied = 'yes' and purchase_amount >= (select AVG(purchase_amount) from customer)

#Which are the top 5 products with the highest average review rating?

select item_purchased, ROUND(AVG(review_rating :: numeric), 2) as "Average Product Rating"
from customer
group by item_purchased
order by AVG(review_rating) desc
limit 5;

#Compare the average purchase amount between standard and express shipping

select shipping_type,
ROUND(AVG(purchase_amount),2)
from customer
where shipping_type in ('Standarad', 'Express')
group by shipping_type

#Do subscribed customers spend more? compare average spend and total revenue between subscribers and non-subscribers

select subscription_status,
COUNT (customer_id) as total_customers,
ROUND (AVG(purchase_amount),2) as avg_spend,
ROUND (SUM(purchase_amount),2) as total_revenue
from customer
group by subscription_status
order by total_revenue, avg_spend desc;

#Which 5 products have the highest percentage of purchases with discounts applied?

select item_purchased,
ROUND(100 * SUM(CASE WHEN LOWER (discount_applied) = 'yes' THEN 1 ELSE 0 END)/COUNT(*),2)
as discount_rate
from customer
group by item_purchased
order by discount_rate desc
limit 5;

#Segment customers into New, Returning, and Loyal based on their total number of pervious purchases, and show the count of each segment

WITH customer_type as (
select 
customer_id, previous_purchases,

CASE
    WHEN previous_purchases= 1 THEN 'New'
	WHEN previous_purchases BETWEEN 2 AND 10 THEN 'Returning'
	ELSE 'Loyal'
	END AS customer_segment
	from customer
	)
select customer_segment, count(*) as "Number of customers"
from customer_type
group by customer_segment;

#What are the top 3 most purchased products within each category?

with item_count as (
select category, item_purchased,
COUNT(customer_id) as total_orders
from customer
group by category, item_purchased
),
ranked_items as (
select category, item_purchased, total_orders,

ROW_NUMBER() over(partition by category order by total_orders desc) as item_rank
from item_count
)
select item_rank, category, item_purchased, total_orders
from ranked_items
where item_rank <=3;

#Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?

select subscription_status,
count (customer_id) as repeat_buyers
from customer
where previous_purchases>5
group by subscription_status

#What is the revenue contribution of each age group?

select age_group,
SUM (purchase_amount) as total_revenue
from customer
group by age_group
order by total_revenue desc;